head.script:
$(document).ready(function(){
    $('#risk_assessment td.color_code').each(function(){
        if ($(this).text() == 3 ) {
            $(this).css('background-color','#FF3535');
        } else if ($(this).text() == 2 ) {
            $(this).css('background-color','#ECE248');
        } else if ($(this).text() == 1 ) {
            $(this).css('background-color','#6BB94F');
        }
    });
});
